mapper.select:
select count(*) from moim
where category=#{category} and city1=#{city1} and city2=#{city2}
where category=#{category} and city1=#{city1}
where category=#{category}
where city1=#{city1} and city2=#{city2}
where city1=#{city1}

select * from moim 
where category=#{category} and city1=#{city1} and city2=#{city2}
where category=#{category} and city1=#{city1}
where category=#{category}
where city1=#{city1} and city2=#{city2}
where city1=#{city1}
order by mnum desc limit #{start},#{perpage}

SELECT moim.* FROM moim LEFT JOIN moimmember ON moim.mnum = moimmember.mnum 
where moim.category=#{category} and moim.city1=#{city1} and moim.city2=#{city2}
where moim.category=#{category} and moim.city1=#{city1}
where moim.category=#{category}
where moim.city1=#{city1} and moim.city2=#{city2}
where moim.city1=#{city1}
GROUP BY moim.mnum ORDER BY COUNT(moimmember.mnum) desc limit #{start},#{perpage}

mapper.update:
update moim set mname=#{mname}

	,mphoto=#{mphoto}

,mcontent=#{mcontent} where mnum=#{mnum}
